import axios from "axios"
import React  from "react"

const url = "http://localhost:8000/artists"
function Freelances() {
	const [post, setPost] = React.useState(null);

	React.useEffect(() => {
		axios.get(url).then((response) => {
			setPost(response.data);
		})
	}, [])
	if (!post) 
		return null;

	let nbr = 20;
	const limite = document.getElementById('nbr');
	const submit = document.getElementById('submit')
	submit.addEventListener('click', function(){
		console.log(limite)
	})
		const tab = [];
		for(let i = 0; i < nbr;i++){
			tab.push(
				<div style={{
					display: 'flex',
					flexDirection: 'column',
					width: '400px'
					}}>
					<img src={post[i].photo} alt="" />
					<h3>{post[i].name}</h3>
					<h6 style={{
						margin: '0'
					}}>{post[i].description}</h6>
					<p style={{
						fontSize: 'xx-small'
					}}>{post[i].bio}</p>
				</div>
			);
		}
    return(

		<div style={{
			display: 'flex',
			flexDirection: 'row',
			flexWrap: 'wrap',
			gap: '16px',
			justifyContent: 'center'
		  }}>
			<input
			type="text"
			value={this.state.myInputValue}
			onChange={e => this.setState({myInputValue: e.target.value})} />
			{tab}
		</div>
    )
}

export default Freelances
