import {useParams} from 'react-router-dom'
import axios from "axios"
import React  from "react"


const url2 = "http://localhost:8000/artists"

function Album(){
	const [post, setPost] = React.useState(null);
	const [post2, setPost2] = React.useState(null);

	const {nbr} = useParams()
	let number = parseInt(nbr) + 1
	let count =  parseInt(nbr)
	const url = `http://localhost:8000/albums/${number}`

	React.useEffect(() => {
		axios.get(url).then((response) => {
			setPost(response.data);
		})
	})

	React.useEffect(() => {
		axios.get(url2).then((response) => {
			setPost2(response.data)
		})
	}, [])

	if(!post)
		return null;
	if(!post2)
		return null;
	console.log(post)
	return(
		<div style={{
			display: 'flex',
			flexDirection: 'row',
			flexWrap: 'wrap',
			gap: '16px',
			justifyContent: 'center'
		  }}>
			<div  style={{
				display: 'flex',
				flexDirection: 'column',
				width: '400px'
				}}>
				<img src={post.cover_small} alt="" />
				<h3>{post.name}</h3>
				<h6 style={{
					margin: '0'
				}}>{post2[count].name}</h6>
				<p style={{
					fontSize: 'xx-small'
				}}>{post.description}</p>
			</div>
		  </div>
	)

}

export default Album
