import {useParams} from 'react-router-dom'
import axios from "axios"
import React  from "react"

const url2 = "http://localhost:8000/albums"
function Genre(){
	const [post, setPost] = React.useState(null)
	const [post2, setPost2] = React.useState(null)


	const {nbr} = useParams();
	let number = parseInt(nbr) + 1;
	const url =  `http://localhost:8000/genres/${number}`

	React.useEffect(() => {
		axios.get(url).then((response) => {
			setPost(response.data);
		})
	}, [])
	React.useEffect(() => {
		axios.get(url2).then((response) => {
			setPost2(response.data);
		})
	}, [])
	if(!post)
		return null;
	if(!post2)
		return null;
	const tab = [];

	for(let i = 0; i < post2.length;i++)
	{
		if(post2[i].id === post.albums[i])
			tab.push(
				<div key={i} style={{
					display: 'flex',
					flexDirection: 'column',
					width: '400px'
					}}>
					<img src={post2[i].cover_small} alt="" />
					<h3>{post2[i].name}</h3>

					<p style={{
						fontSize: 'xx-small'
					}}>{post2[i].description}</p>
				</div>
			)
	}

	console.log(post)
	console.log(post2)

	return(
		<div>
			<h1>{post.genre.name}</h1>
		{tab}
		</div>

	)
}


export default Genre
