import {useParams} from 'react-router-dom'
import axios from "axios"
import React  from "react"


const url2 = "http://localhost:8000/artists"

function Album(){
	const [post, setPost] = React.useState(null);
	const [post2, setPost2] = React.useState(null);

	const {nbr} = useParams()
	let number = parseInt(nbr) + 1
	const url = `http://localhost:8000/albums/${number}`

	React.useEffect(() => {
		axios.get(url).then((response) => {
			setPost(response.data);
		})
	}, [])

	React.useEffect(() => {
		axios.get(url2).then((response) => {
			setPost2(response.data)
		})
	}, [])

	if(!post)
		return null;
	if(!post2)
		return null;
	console.log(post)
	const tab = [];
	for(let i = 0; i < nbr;i++){
		tab.push(
			<div key={i} style={{
				display: 'flex',
				flexDirection: 'column',
				width: '400px'
				}}>
				<img src={post[i].cover_small} alt="" />
				<h3>{post[i].name}</h3>
				<h6 style={{
					margin: '0'
				}}>{post2[i].name}</h6>
				<p style={{
					fontSize: 'xx-small'
				}}>{post[i].description}</p>
			</div>
		);
	}
	return(
		<h2>{number}</h2>
	)

}

export default Album
